def word_count(text):
    words = text.split()
    # If 'words' is empty, it means the input 'text' was either empty
    # or consisted entirely of whitespace characters.
    # The failing test 'test_word_count_leading_trailing_spaces' suggests
    # that a non-empty string consisting only of whitespace should count as 1 word.
    # An empty string "" should still count as 0 words.
    if not words and text: # 'text' evaluates to True if it's a non-empty string
        return 1
    return len(words)

